import React, { useState, useEffect } from "react";

import "./card2.scss";

import { Button } from "../index";

type Props = JSX.IntrinsicElements["div"] & {
  title?: string;
  desc?: string;
  timerStart?: boolean;
};

const Card2: React.FC<Props> = ({ title, timerStart, desc, ...rest }) => {
  const [timer, setTimer] = useState(0);

  const [sa, setSa] = useState(0);
  const [dk, setDk] = useState(0);
  const [stopCount, setStopCount] = useState(true);

  useEffect(() => {
    stopCount
      ? setTimeout(() => {
          if (timer >= 60) {
            setTimer(0);
            setDk(dk + 1);
          } else {
            setTimer(timer + 1);
          }
          if (dk >= 60) {
            setDk(0);
            setSa(sa + 1);
          }
        }, 1000)
      : console.log("nothing");
  }, [timer]);

  return (
    <div className="card2">
      <div className="card2-leftside">
        <p className="card2-leftside-title">{title ? title : "default"}</p>
        <p className="card2-leftside-text">{desc ? desc : "default"}</p>
      </div>

      <p className="card2-count">
        {sa ? sa : "0"}.{dk ? dk : "0"}.{timer} dk
      </p>

      <div className="card2-buttons">
        <Button blue onClick={() => setStopCount(false)}>
          Durdur
        </Button>
        <Button green onClick={() => setStopCount(true)}>
          Devam
        </Button>
        <Button
          red
          onClick={() => {
            setStopCount(false);
            clearTimeout();
            setTimer(0);
            setDk(0);
            setSa(0);
          }}
        >
          Sıfırla
        </Button>
      </div>
    </div>
  );
};

export default Card2;
